        private Point[] spawnPositions;

        public Level1()
        {
            Player = new Player();
            StartPosition = new Vector2(1000, 100);
            Player.Position = StartPosition;
            LevelMap = new Map(mapStructure);
            Entities = new List<Entity>() { Player };
            EntityAddList = new HashSet<Entity>();
            EntityRemoveList = new HashSet<Entity>();
            Zombies = new List<Zombie>();
            spawnPositions = new Point[5] { new Point(100, 100), new Point(400, 800), new Point(900, 500), new Point(1750, 500), new Point(900, 400) };
            InitializeZombies();
        }

        public override void RestartLevel(GameModel game)
        {
            game.CurrentLevel = new Level1();
        }

        private void InitializeZombies()
        {
            for (int i = 0; i < 5; i++)
            {
                var position = spawnPositions[i];
                Zombies.Add(new Zombie(position, i % 3, mapStructure));
            }
        }

